#ENTER WALLET ADDRESS
myAddress = "INSERT YOUR WALLET ADDRESS HERE" #<---- YOUR WALLET ADDRESS

#Checking Wallet Balance
myWalletBalance = web3.eth.get_balance(myAddress)
humanReadableBalance=web3.fromWei(myWalletBalance,'ether')
print("\nCurrent wallet balance :" , humanReadableBalance , "ETH")
print(" \n \n This Program is used to purchase tokens to your wallet")

#Input ETH that will be spent
print("How much ETH do you want to swap : ")
ethAmount = float(input())
while float(ethAmount)>float(humanReadableBalance) :
    print("Need more ETH for this transaction,check Current Wallet Balance and try again:")
