#GAS VERIFICATION
print("\n For the Gas price, \n L = LOW (125 GWEI) \n M = MEDIUM (135 GWEI) \n H = HIGH (200 GWEI) \n UH = ULTRAHIGH (250 GWEI)")
gasInput = str(input("\n \n Please Choose your Gas Price (L,M,H,UH) : "))
while gasInput != 'L' and gasInput != 'M' and gasInput != 'H' and gasInput != 'UH':
    print('Wrong Input, \n make sure you only enter L, M , H or UH')
    gasInput = (input("\n \nPlease Choose your Gas Price (L,M,H,UH) : "))

if gasInput == "L":
    priceOfGas = 125
if gasInput == "M":
    priceOfGas = 135
if gasInput == "H":
    priceOfGas = 200
if gasInput == "UH":
    priceOfGas = 250

print("\n Your Gas Price is:", priceOfGas, "GWEI\n")


